mapper.select:
select

from tb_permission p
where p.is_deleted = 0

    AND p.attr_id =#{attrId,jdbcType=INTEGER}


    AND p.father_id =#{fatherId,jdbcType=INTEGER}


    AND p.permission_type =#{permissionType,jdbcType=INTEGER}

SELECT DISTINCT

FROM tb_users tus,tb_user_role tur,tb_role_permission trp,tb_permission p
WHERE tus.user_no=tur.user_no
AND tur.role_id=trp.role_id AND trp.permission_id=p.id
AND tus.is_deleted=0        AND tur.is_deleted=0
AND trp.is_deleted=0        AND p.is_deleted=0
AND trp.is_show=1

    AND tus.user_no =#{userNo,jdbcType=VARCHAR}


    AND tur.merc_id=#{merchantId,jdbcType=VARCHAR}


    AND p.ps_number=#{psNumber,jdbcType=VARCHAR}
